/**
 * Generate version info banner
 */
export function generateVersionBanner() {
    const version = getPackageVersion();
    return boxen(chalk.hex('#00d2d3')('ğŸ“¦ Version: ') + chalk.hex('#ffa502')(`v${version}`) +
        chalk.hex('#95afc0')('  â€¢  ') + chalk.hex('#00d2d3')('ğŸ¯ Frameworks: ') + chalk.hex('#ffa502')('12+') +
        chalk.hex('#95afc0')('  â€¢  ') + chalk.hex('#00d2d3')('ğŸ“‹ Templates: ') + chalk.hex('#ffa502')('50+') +
        chalk.hex('#95afc0')('  â€¢  ') + chalk.hex('#00d2d3')('âš¡ Status: ') + chalk.hex('#10ac84')('Ready to scaffold!'), {
        padding: { top: 0, bottom: 0, left: 2, right: 2 },
        borderStyle: 'round',
        borderColor: 'cyan',
        backgroundColor: '#0a0a0a'
    });
}
